' set the name of the SSH server( its URL ) sftp.HostAddress = "some.sftp.server.com" ' replace with your SFTP server sftp.UserName = "anonymous" ' replace with your user name sftp.Password = "user@mail.com" ' replace with your password
Dim myStream As Stream = File.OpenWrite("c:\uploadfile1.txt") sftp.DownloadStream(myStream, "uploadfile1.txt", 2500) myStream.Close()

SFTP sftp = new SFTP(); //Trial Mode            
//SFTP sftp = new SFTP("place user name here", "place license key here");

// set the name of the SSH server( its URL )
sftp.HostAddress = "some.sftp.server.com"; // replace with your SFTP server
sftp.UserName  = "anonymus"; // replace with your user name
sftp.Password = "user@mail.com"; // replace with your password

System.Console.WriteLine("We are downloading the a.txt file.This is a blocking call");

sftp.DownloadFileAsync("c:\\a.txt", "a.txt");

while (sftp.IsBusy == true)
{
    System.Threading.Thread.Sleep(100);
}

System.Console.WriteLine("{0} files have been transfered", sftp.FileCount);

